import React from "react";
import { Col, Row } from "react-bootstrap";

export const LojaFiltro = (props) => {
  let { titulo, filtros } = props;

  const capitalize = (string) => {
    string = string.toLowerCase();

    return [...string.slice(0, 1).toUpperCase(), ...string.slice(1)].join("");
  };

  titulo = titulo
    .split(" ")
    .map((string) => capitalize(string))
    .join(" ");

  let filtrosKeys = Object.keys(filtros);
  console.log(filtrosKeys);

  return (
    <Col xs={true}>
      <h5>{titulo}</h5>
      {filtrosKeys.map((filtroKey) => {
        return (
          <Row>
            <Col
              xs={true}
              style={{
                padding: "0px",
                width: "max-content",
                maxWidth: "none",
              }}
            >
              <input
                type="checkbox"
                id="vehicle1"
                name="vehicle1"
                value="Bike"
              />
              <label for="vehicle1"> {filtroKey}</label> <br />
            </Col>
          </Row>
        );
      })}
    </Col>
  );
};
