import { setFiltros, setOrdemAtiva, setProdutosAtivos } from "../Actions";

export const SetOrdem = (type) => {
  return async (dispatch, getState) => {
    let produtos = getState().loja.produtosAtivos;
    let produtosAtivos = [];
    switch (type) {
      case "nomeAZ":
        produtosAtivos = produtos.sort((x, y) => {
          let a = x.nomeVnda;
          let b = y.nomeVnda;

          return a === b ? 0 : a > b ? 1 : -1;
        });
        break;
      case "nomeZA":
        produtosAtivos = produtos.sort((x, y) => {
          let a = x.nomeVnda;
          let b = y.nomeVnda;

          return a === b ? 0 : a < b ? 1 : -1;
        });
        break;
      case "precoCrescente":
        produtosAtivos = produtos.sort((x, y) => {
          let a = x.valor;
          let b = y.valor;

          return a === b ? 0 : a > b ? 1 : -1;
        });
        break;
      case "precoDecresente":
        produtosAtivos = produtos.sort((x, y) => {
          let a = x.valor;
          let b = y.valor;

          return a === b ? 0 : a < b ? 1 : -1;
        });

        break;
      default:
        produtosAtivos = produtos;
        break;
    }

    dispatch(setOrdemAtiva(type));
    dispatch(setProdutosAtivos(produtosAtivos));
  };
};
