import React from "react";
import { Col, Row } from "react-bootstrap";
import { useSelector } from "react-redux";

export const LojaFiltro = (props) => {
  let { titulo, filtros } = props;
  const capitalize = (string) =>
    [...string.slice(0, 1).toUpperCase(), ...string.slice(1)].join("");

  titulo = titulo
    .split(" ")
    .map((string) => capitalize(string))
    .join(" ");
  let filtrosKeys = [];
  if (filtros) {
    filtrosKeys = Object.keys(filtros);
  }
  return (
    <Col xs={true}>
      <h5>{titulo}</h5>
      {filtrosKeys.map((filtroKey) => {
        return (
          <Row>
            <Col
              xs={true}
              style={{
                padding: "0px",
                width: "max-content",
                maxWidth: "none",
              }}
            >
              <input
                type="checkbox"
                id="vehicle1"
                name="vehicle1"
                value="Bike"
              />
              <label htmlFor="vehicle1"> {filtroKey}</label> <br />
            </Col>
          </Row>
        );
      })}
    </Col>
  );
};
