import React from "react";
import { Button, Col, Row } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { AiOutlineCheck } from "react-icons/ai";
import { VscLoading } from "react-icons/vsc";
import { changePage } from "./../../../Redux/Actions/Actions";
import { BiStore } from "react-icons/bi";
import { LojaFilters } from "./LojaFilters";

export const ProductGridHeader = () => {
  const saving = useSelector((state) => state.config.saving);
  const dispatch = useDispatch();

  return (
    <Row className="align-items-end justify-content-end barra-filtros">
      <Col className="text-center" sm={{ span: 2, offset: 5 }}>
        {saving ? (
          <p
            style={{
              width: "max-content",
              marginBottom: "0px",
              color: "#ff0018a8",
            }}
          >
            Salvando Orçamento... <VscLoading />{" "}
          </p>
        ) : (
          <p
            style={{
              width: "max-content",
              marginBottom: "0px",
              color: "#15cb00c7",
            }}
          >
            Orçamento Salvo <AiOutlineCheck />{" "}
          </p>
        )}
      </Col>
      {/* <Col sm={true}>
        <LojaFilters />
      </Col> */}
      <Col sm={true} className="text-end">
        <Button
          variant="outline-light"
          className="me-1"
          onClick={() => dispatch(changePage("orcamentoAtual"))}
        >
          <h6>Orcamento Atual</h6>
        </Button>
        <Button
          variant="outline-light"
          onClick={() => dispatch(changePage("orcamentoAtual"))}
        >
          <h6>Filtros</h6>
        </Button>
      </Col>
    </Row>
  );
};
