import React, { useEffect, useState } from "react";
import { Button, Col, Figure } from "react-bootstrap";
import { BiMinus, BiPlus } from "react-icons/bi";
import { useDispatch } from "react-redux";
import "./ProductGridProduct.css";
export const ProductGridProduct = (props) => {
  let baseLink = "https://buckettelexpress.s3.amazonaws.com/FOTOS+GOFIND/";
  const dispatch = useDispatch();
  const [ativo, setAtivo] = useState(false);
  const [quantidade, setQuantidade] = useState(0);
  let decrement = () => {
    if (quantidade > 0) {
      let quant = quantidade - props.produto.multiplo;
      setQuantidade(quant);

      if (quant > 0) {
        setAtivo(true);
      } else {
        setAtivo(false);
      }
    }
  };
  let increment = () => {
    setQuantidade(quantidade + props.produto.multiplo);
    setAtivo(true);
  };
  useEffect(() => {
    if (props.orcamentoAtual.itens) {
      let produto = props.orcamentoAtual.itens.find((item) => {
        return props.produto.sku === item.sku;
      });
      if (produto) {
        setAtivo(true);
        setQuantidade(produto.quantidade);
      }
    }
  }, [props]);
  return (
    <Col xs={3} className="text-start">
      <Figure>
        <a
          target="_blank"
          rel="noreferrer"
          href={baseLink + props.produto.sku + ".jpg"}
        >
          <Figure.Image
            alt={props.produto.nome}
            src={baseLink + props.produto.sku + ".jpg"}
            style={{ borderRadius: "20px" }}
          />
        </a>{" "}
        <Figure.Caption style={{ color: "white" }}>
          <div style={{ textAlign: "-webkit-center", padding: "10px" }}>
            <div
              className="quantity-input"
              style={
                ativo
                  ? {
                      backgroundColor: "gold",
                      color: "darkblue",
                      width: "fit-content",
                    }
                  : {
                      backgroundColor: "white",
                      color: "darkblue",
                      width: "fit-content",
                    }
              }
            >
              <button
                onClick={decrement}
                className="quantity-input__modifier quantity-input__modifier--left"
                style={{ color: "inherit" }}
              >
                <BiMinus />
              </button>
              <input
                className="quantity-input__screen"
                type="text"
                value={quantidade}
                readOnly
                style={{ color: "inherit" }}
              />
              <button
                onClick={increment}
                style={{ color: "inherit" }}
                className="quantity-input__modifier quantity-input__modifier--right"
              >
                <BiPlus />
              </button>
            </div>
          </div>
        </Figure.Caption>
        <Figure.Caption style={{ color: "white" }}>
          {props.produto.nome}
        </Figure.Caption>
        <Figure.Caption style={{ color: "white" }}>
          Referencia: {props.produto.sku}
        </Figure.Caption>
        <Figure.Caption style={{ color: "white" }}>
          Preco Unitario: R$ {props.produto.valor}
        </Figure.Caption>
        <Figure.Caption style={{ color: "white" }}>
          No Meu Orcamento: R$ {(props.produto.valor * quantidade).toFixed(2)}
        </Figure.Caption>
      </Figure>
    </Col>
  );
};
