import React from "react";
import { Button, Col, Figure, Row } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { QuantityInput } from "./QuantityInput";

export const ProductGrid = () => {
  let baseLink = "https://buckettelexpress.s3.amazonaws.com/FOTOS+GOFIND/";
  const dispatch = useDispatch();
  const produtos = useSelector((state) => state.orcamentos.produtos);
  return (
    <div className="bodie">
      <Row
        style={{ height: "100%", minHeight: "100vh", paddingTop: "20px" }}
        className="justify-content-center align-content-start"
      >
        <Col xs={11}>
          <Row className="text-center">
            <Col style={{ padding: "0px 0px 30px 0px" }}>
              <h1>Produtos</h1>
            </Col>
          </Row>
          <Row className="justify-content-center">
            {produtos.map((produto, index) => {
              if (index < 12) {
                return (
                  <Col
                    key={index}
                    xs={3}
                    className="text-start"
                    style={{ paddingTop: "20px" }}
                  >
                    <Figure>
                      <a
                        target="_blank"
                        rel="noreferrer"
                        href={baseLink + produto.sku + ".jpg"}
                      >
                        <Figure.Image
                          alt={produto.nome}
                          src={baseLink + produto.sku + ".jpg"}
                          style={{ borderRadius: "20px" }}
                        />
                      </a>
                      <Figure.Caption style={{ color: "white" }}>
                        <QuantityInput />
                      </Figure.Caption>
                      <Figure.Caption style={{ color: "white" }}>
                        <Button
                          variant="outline-light"
                          style={{ width: "100%" }}
                        >
                          Adicionar ao Orcamento
                        </Button>
                      </Figure.Caption>
                      <Figure.Caption style={{ color: "white" }}>
                        {produto.nome}
                      </Figure.Caption>
                      <Figure.Caption style={{ color: "white" }}>
                        Preco: ${produto.valor}
                      </Figure.Caption>
                      <Figure.Caption style={{ color: "white" }}>
                        SKU: {produto.sku}
                      </Figure.Caption>
                    </Figure>
                  </Col>
                );
              }
            })}
          </Row>
        </Col>
      </Row>
    </div>
  );
};
