import { trocarItem } from "../Actions";
import { saveOrcamento } from "../SaveOrcamento";

export const ChangeProduto = (index, indexOrcamento, sku) => {
  return async (dispatch, getState) => {
    let produtos = getState().orcamentos.produtos;
    if (sku && sku.toString().length > 5 && sku / 6000 > 1) {
      console.log(produtos);
      console.log(sku);
      let r = produtos.find((produto) => {
        return produto.sku === parseInt(sku);
      });
      console.log(r);
      state.salvos[action.indexOrcamento].itens[index] = {
        ...state.salvos[action.indexOrcamento].itens[index],
        ...action.infos,
        valorReal: 0,
      };

      await dispatch(trocarItem(r, index, indexOrcamento));
      dispatch(saveOrcamento({ id: indexOrcamento }));
    } else {
      await dispatch(
        trocarItem(
          {
            sku,
            nome: "",
            valor: 0,
            valorReal: 0,
            quantidade: 0,
            preco: 0,
            multiplo: 0,
            caixaMaster: 0,
            estoque: "",
          },
          index,
          indexOrcamento
        )
      );
    }
  };
};
