import { addItem } from "../Actions";
import { saveOrcamento } from "../SaveOrcamento";

export const AdicionarProduto = (produto) => {
  return async (dispatch, getState) => {
    let indexOrcamento = getState().orcamentos.salvos.findIndex(
      (orcamento) => orcamento.id === orcamentos.atual
    );
    dispatch(addItem(indexOrcamento, produto));
    dispatch(saveOrcamento({ index: indexOrcamento }));
  };
};
