const reducerLoja = (
  state = {
    filtros: {},
  },
  action
) => {
  switch (action.type) {
    case "SETFILTROS":
      return {
        ...state,
        filtros: { ...action.filtros },
      };

    default:
      return state;
  }
};
export default reducerLoja;
