import React from "react";
import { Button, Col, Figure } from "react-bootstrap";
import { ProductGridQuantityInput } from "./ProductGridQuantityInput";

export const ProductGridProduct = (props) => {
  let baseLink = "https://buckettelexpress.s3.amazonaws.com/FOTOS+GOFIND/";
  return (
    <Col xs={3} className="text-start" style={{ paddingTop: "20px" }}>
      <Figure>
        <a
          target="_blank"
          rel="noreferrer"
          href={baseLink + props.produto.sku + ".jpg"}
        >
          <Figure.Image
            alt={props.produto.nome}
            src={baseLink + props.produto.sku + ".jpg"}
            style={{ borderRadius: "20px" }}
          />
        </a>
        <Figure.Caption style={{ color: "white" }}>
          <div
            className="quantity-input"
            style={{ backgroundColor: "transparent" }}
          >
            <button className="quantity-input__modifier quantity-input__modifier--left">
              <BiMinus />
            </button>
            <input
              className="quantity-input__screen"
              type="text"
              value={1}
              readOnly
            />
            <button className="quantity-input__modifier quantity-input__modifier--right">
              <BiPlus />
            </button>
          </div>
        </Figure.Caption>
        <Figure.Caption style={{ color: "white" }}>
          <Button variant="outline-light" style={{ width: "100%" }}>
            Adicionar ao Orcamento
          </Button>
        </Figure.Caption>
        <Figure.Caption style={{ color: "white" }}>
          {props.produto.nome}
        </Figure.Caption>
        <Figure.Caption style={{ color: "white" }}>
          Preco: ${props.produto.valor}
        </Figure.Caption>
        <Figure.Caption style={{ color: "white" }}>
          SKU: {props.produto.sku}
        </Figure.Caption>
      </Figure>
    </Col>
  );
};
