import React, { useEffect, useState } from "react";
import { Col, Figure } from "react-bootstrap";
import { BiMinus, BiPlus } from "react-icons/bi";
import LazyLoad from "react-lazyload";
import { useDispatch, useSelector } from "react-redux";
import { AddItens } from "../../../../Redux/Actions/TabelaActions/AddItens";
import { ChangeQuantidade } from "../../../../Redux/Actions/TabelaActions/ChangeQuantidade";
import { DeleteItens } from "../../../../Redux/Actions/TabelaActions/DeleteItens";
import "./ProductGridProduct.css";
export const ProductGridProduct = (props) => {
  let baseLink = "https://buckettelexpress.s3.amazonaws.com/FOTOS+GOFIND/";
  const dispatch = useDispatch();
  const [ativo, setAtivo] = useState(false);
  const [quantidade, setQuantidade] = useState(0);
  const orcamentos = useSelector((state) => state.orcamentos);
  useEffect(() => {
    let orcamentoAtual = orcamentos.salvos.find(
      (orcamento) => orcamento.id === orcamentos.atual
    );
    let produto = orcamentoAtual.itens.find((item) => {
      return item.sku === props.produto.sku;
    });
    console.log(produto);
    if (!produto) {
      setQuantidade(0);
      setAtivo(false);
    } else {
      setQuantidade(produto.quantidade);
      setAtivo(true);
    }
  }, [orcamentos]);

  let decrement = () => {
    if (quantidade > props.multiplo) {
      dispatch(
        ChangeQuantidade(props.produto.sku, quantidade + props.produto.multiplo)
      );
    } else if (quantidade > 0) {
      dispatch(DeleteItens(props.produto.sku));
    }
  };
  let increment = () => {
    dispatch(AddItens(props.produto.sku, quantidade + props.produto.multiplo));
    setQuantidade(quantidade + props.produto.multiplo);
  };
  return (
    <Col xs={6} className="text-start">
      <Figure style={{ backgroundColor: "white", borderRadius: "10px" }}>
        <a
          target="_blank"
          rel="noreferrer"
          href={baseLink + props.produto.sku + ".jpg"}
        >
          <LazyLoad height={200}>
            <img
              className="img-fluid"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = "errorImage.jpg";
              }}
              alt={props.produto.nome}
              src={baseLink + props.produto.sku + ".jpg"}
              style={{ borderRadius: "20px" }}
            />
          </LazyLoad>
        </a>{" "}
        <Figure.Caption style={{ color: "black" }}>
          <div style={{ textAlign: "-webkit-center", padding: "10px" }}>
            <div
              className="quantity-input"
              style={
                ativo
                  ? {
                      backgroundColor: "gold",
                      color: "white",
                      width: "fit-content",
                    }
                  : {
                      backgroundColor: "black",
                      color: "white",
                      width: "fit-content",
                    }
              }
            >
              <button
                onClick={decrement}
                className="quantity-input__modifier quantity-input__modifier--left"
                style={{ color: "inherit" }}
              >
                <BiMinus />
              </button>
              <input
                className="quantity-input__screen"
                type="text"
                value={quantidade ? quantidade : 0}
                readOnly
                style={{ color: "inherit" }}
              />
              <button
                onClick={increment}
                style={{ color: "inherit" }}
                className="quantity-input__modifier quantity-input__modifier--right"
              >
                <BiPlus />
              </button>
            </div>
          </div>
        </Figure.Caption>
        <Figure.Caption>{props.produto.nome}</Figure.Caption>
        <Figure.Caption>Referencia: {props.produto.sku}</Figure.Caption>
        <Figure.Caption>
          Preco Unitario: R$ {props.produto.valor}
        </Figure.Caption>
        <Figure.Caption>
          No Meu Orcamento: R$ {(props.produto.valor * quantidade).toFixed(2)}
        </Figure.Caption>
      </Figure>
    </Col>
  );
};
