import React, { useEffect, useState } from "react";
import { Col, Row } from "react-bootstrap";
import { useSelector } from "react-redux";
import { ProductGridHeader } from "./ProductGridHeader";
import { ProductGridProduct } from "./ProductGridProduct";
import CornerRibbon from "react-corner-ribbon";

export const ProductGrid = () => {
  const produtos = useSelector((state) => state.orcamentos.produtos);
  const orcamentos = useSelector((state) => state.orcamentos);
  let { titulo } = orcamentos.salvos.find(
    (orcamento) => orcamento.id === orcamentos.atual
  );
  return (
    <div className="bodie">
      <Row
        style={{ height: "100%", minHeight: "100vh", paddingTop: "20px" }}
        className="justify-content-center align-content-start"
      >
        <Col xs={11}>
          <Row className="text-center">
            <Col style={{ padding: "0px 0px 30px 0px" }}>
              <h1>{titulo}</h1>
            </Col>
          </Row>
          <ProductGridHeader />

          <Row className="justify-content-center">
            {produtos.map((produto, index) => {
              if (index) {
                return (
                  <ProductGridProduct
                    key={index}
                    index={index}
                    produto={produto}
                  />
                );
              }
            })}
          </Row>
        </Col>
      </Row>
    </div>
  );
};
