import React, { useEffect, useState } from "react";
import { Col, Row } from "react-bootstrap";
import LazyLoad from "react-lazyload";
import { useSelector } from "react-redux";
import { ProductGridProduct } from "./ProductGridProduct";

export const ProductGrid = () => {
  const produtos = useSelector((state) => state.orcamentos.produtos);
  const orcamentos = useSelector((state) => state.orcamentos);
  const [orcamentoAtual, setOrcamentoAtual] = useState({});
  useEffect(() => {
    if (orcamentos.atual) {
      setOrcamentoAtual(
        orcamentos.salvos.find((orcamento) => orcamento.id === orcamentos.atual)
      );
    }
    console.log(orcamentoAtual);
  }, [orcamentos]);
  return (
    <div className="bodie">
      <Row
        style={{ height: "100%", minHeight: "100vh", paddingTop: "20px" }}
        className="justify-content-center align-content-start"
      >
        <Col xs={11}>
          <Row className="text-center">
            <Col style={{ padding: "0px 0px 30px 0px" }}>
              <h1>Orcamento {orcamentoAtual.titulo}</h1>
            </Col>
          </Row>
          <Row className="justify-content-center">
            <LazyLoad>
              {produtos.map((produto, index) => {
                if (index < 12) {
                  return (
                    <ProductGridProduct
                      key={index}
                      index={index}
                      produto={produto}
                      orcamentoAtual={orcamentoAtual}
                    />
                  );
                }
              })}
            </LazyLoad>
          </Row>
        </Col>
      </Row>
    </div>
  );
};
