const reducerDatabank = (
  state = {
    userInfo: {},
    produtos: [],
    orcamentosPrime: [],
    orcamentosVnda: [],
  },
  action
) => {
  switch (action.type) {
    case "UPDATEDATABANK":
      return { ...state, ...action.input };
    case "UPDATEORCAMENTOPRIME":
      let orcamentoArray = [...state.orcamentosPrime];
      orcamentoArray[action.indexOrcamento] = action.orcamento;
      return { ...state, orcamentosPrime: [...orcamentoArray] };
    case "ADDORCAMENTO":
      state.orcamentosPrime = [...state.orcamentosPrime, action.orcamento];
      return { ...state };

    default:
      return state;
  }
};
export default reducerDatabank;
