import { setFiltros } from "../Actions";

export const SetFiltros = () => {
  return async (dispatch, getState) => {
    let produtos = getState().databank.produtos;
    let tags = {};

    const capitalize = (string) => {
      string = string.toLowerCase();

      return [...string.slice(0, 1).toUpperCase(), ...string.slice(1)].join("");
    };

    for (let i = 0; i < produtos.length; i++) {
      const produto = produtos[i];
      let tagKeys = Object.keys(produto.tags);
      for (let j = 0; j < tagKeys.length; j++) {
        const tagKey = tagKeys[j];
        let valor = produto.tags[tagKey];
        let quantidade = 1;

        if (tags[tagKey][valor]) {
          quantidade = quantidade + tags[tagKey][valor].quantidade;
          tags[tagKey] = {
            ...tags[tagKey],
            [valor]: {
              ...tags[tagKey][valor],
              quantidade,
            },
          };
        } else {
          tags[tagKey] = {
            [valor]: {
              checked: false,
              ativo: true,
              quantidade,
            },
          };
        }
      }
    }
    dispatch(setFiltros(tags));
  };
};
