import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { ChangeProduto } from "../../../../../Redux/Actions/TabelaActions/ChangeSku";
import { Quantity } from "./Quantity";
import { BsTrash } from "react-icons/bs";
import { ChangeQuantidade } from "../../../../../Redux/Actions/TabelaActions/ChangeQuantidade";
import { DeleteItens } from "../../../../../Redux/Actions/TabelaActions/DeleteItens";

export const TRow = (props) => {
  const dispatch = useDispatch();
  const Itens = useSelector(
    (state) => state.orcamentos.salvos[props.indexOrcamento].itens[props.index]
  );
  const handleChange = (e) => {
    dispatch(ChangeSku(props.index, e.target.value));
  };

  const handleEnter = (e) => {
    switch (e.key) {
      case "Enter":
        let nextSibling = document.querySelector(
          `input[name=input-${props.index + 1}]`
        );
        if (nextSibling) {
          nextSibling.focus();
        } else {
          (async () => {
            await dispatch(ChangeQuantidade());
            nextSibling = document.querySelector(
              `input[name=input-${props.index + 1}]`
            );
            nextSibling.focus();
          })();
        }
        break;
      case "ArrowRight":
        let SiblingRight = document.querySelector(
          `input[name=input-quantity-${props.index}]`
        );
        if (e.shiftKey) {
          SiblingRight.focus();
        }

        break;
      case "ArrowUp":
        let SiblingUp = document.querySelector(
          `input[name=input-${props.index - 1}]`
        );
        if (SiblingUp) {
          SiblingUp.focus();
        }
        break;
      case "ArrowDown":
        let SiblingDown = document.querySelector(
          `input[name=input-${props.index + 1}]`
        );
        if (SiblingDown) {
          SiblingDown.focus();
        }
        break;
      default:
        break;
    }
  };

  return (
    <tr className={props.index % 2 === 0 ? "tdWhite" : "tdBlack"}>
      <td id="td" className="tdIndex">
        {props.index + 1}.
      </td>
      <td id="td" className="tdSku">
        <div key={Itens.sku}>
          <input
            autoFocus
            className="table-input"
            autoComplete="off"
            name={"input-" + props.index}
            defaultValue={Itens.sku !== 0 ? Itens.sku : ""}
            onChange={handleChange}
            onKeyDown={handleEnter}
          />
        </div>
      </td>
      <td>{Itens.nome}</td>
      <td className="tdCaixaMaster">
        {Itens.caixaMaster ? Itens.caixaMaster + " un" : ""}
      </td>
      <td className="tdDescontoCaixaMaster">
        {Itens.quantidade % Itens.caixaMaster === 0 && Itens.quantidade > 0
          ? "5%"
          : ""}
      </td>
      <td className="tdValor">
        {Itens.valor && Itens.valor !== "NaN" ? (
          <>
            {" "}
            <span>R$</span> <span> {parseFloat(Itens.valor).toFixed(2)} </span>{" "}
          </>
        ) : (
          ""
        )}
      </td>
      <td className="tdQuantidade">
        {Itens.nome ? (
          <Quantity index={props.index} indexOrcamento={props.indexOrcamento} />
        ) : (
          ""
        )}
      </td>
      <td className="tdPreco">
        {Itens.nome
          ? "R$" + parseFloat(Itens.valor * Itens.quantidade).toFixed(2)
          : ""}
      </td>
      <td className="tdEstoque">
        {Itens.nome ? (Itens.estoque > 0 ? "Disponível" : "Indisponível") : ""}
      </td>
      <td className="tdTrash">
        <button
          onClick={() => dispatch(DeleteItens(Itens.sku))}
          style={{
            border: "none",
            backgroundColor: "transparent",
            color: "white",
          }}
        >
          <BsTrash />
        </button>
      </td>
    </tr>
  );
};
