const reducerLoja = (
  state = {
    filtros: {},
    produtosAtivos: [],
  },
  action
) => {
  switch (action.type) {
    case "SETFILTROS":
      return {
        ...state,
        filtros: { ...action.filtros },
      };
    case "SETPRODUTOSATIVOS":
      return {
        ...state,
        produtosAtivos: { ...action.produtosAtivos },
      };

    default:
      return state;
  }
};
export default reducerLoja;
