import { setFiltros } from "../Actions";

export const ToggleFiltros = (mlce, categorias, isChecked) => {
  return async (dispatch, getState) => {
    let produtos = getState().databank.produtos;
    let filtros = getState().loja.filtros;

    console.log(mlce);
    console.log(categorias);
    console.log(isChecked);

    filtros[mlce.toLowerCase()][categorias].checked = isChecked;

    // produtos.filter((produto) => {
    //   return produto.tags[mlce] === categorias;
    // });
    dispatch(setFiltros(filtros));
  };
};
