import {
  changePage,
  loading,
  savingOrcamento,
  toggleModal,
  updateOrcamentos,
} from "./Actions";
import callBackend from "./CallBackend";
import { ChangePage } from "./ChangePage";
var crypto = require("crypto");

export const saveOrcamento = (orcamento) => {
  return (dispatch, getState) => {
    try {
      let pedido = { ...orcamento };
      dispatch(savingOrcamento(true));
      let { socket } = getState().pages;
      let { salvos } = getState().orcamentos;
      let token = localStorage.getItem("token");
      let id;
      if (Object.keys(orcamento)[[0]] === "id") {
        orcamento = salvos.find((orc) => {
          return orc.id === orcamento.id;
        });
      } else if (Object.keys(orcamento)[[0]] === "index") {
        orcamento = salvos[orcamento.index];
      } else if (Object.keys(orcamento)[[0]] === "titulo") {
        dispatch(loading(true));
        id = crypto.randomBytes(20).toString("hex").toUpperCase();
        orcamento = {
          id,
          titulo: orcamento.titulo,
          pagamentoAntecipado: false,
          itens: [],
          criadoEm: new Date(),
          ultimaModificacao: new Date(),
        };
      }
      let { subTotal, subTotalDisponivel, total, totalDisponivel, ...rest } =
        orcamento;
      orcamento = { ...rest, ultimaModificacao: new Date() };
      callBackend(socket, "saveOrcamento", token, { ...orcamento }).then(
        async (r) => {
          dispatch(savingOrcamento(false));
          dispatch(loading(false));
          if (Object.keys(pedido)[[0]] === "titulo") {
            await dispatch(updateOrcamentos({ salvos: r.orcamentos }));
            await dispatch(updateOrcamentos({ atual: id }));
            await dispatch(ChangePage("orcamentoAtual"));
            await dispatch(toggleModal("criarOrcamento", false));
          }
        }
      );
    } catch (e) {
      console.log(e);
    }
  };
};
