import { changePage, loading, toggleModal, updateOrcamentos } from "./Actions";
import callBackend from "./CallBackend";

export const criarOrcamento = (titulo) => {
  return async (dispatch, getState) => {
    // dispatch(loading(true));
    let { socket } = getState().pages;
    let token = localStorage.getItem("token");
    await callBackend(socket, "newOrcamento", token, { titulo });
    // await dispatch(updateOrcamentos({ atual: id }));
    await dispatch(toggleModal("criarOrcamento", false));
    await dispatch(loading(false));

    // await dispatch(changePage("orcamentoAtual"));
    // await dispatch(toggleModal("informacoes", true));

    Promise.resolve();
  };
};
