const reducerConfig = (
  state = {
    currentPage: "home",
    orcamentoAtivo: 0,
    socket: {},
    sideBar: false,
    loading: false,
    saving: false,
    errors: { login: false, pedidoMinimo: false },
    modals: {
      criarOrcamentoBranco: false,
      criarOrcamentoVnda: false,
      instrucoes: false,
    },
  },
  action
) => {
  let { type, ...rest } = action;
  switch (type) {
    case "CHANGEPAGE":
    case "CHANGEORCAMENTOATIVO":
    case "ADDSOCKET":
    case "TOGGLESIDEBAR":
    case "TOGGLELOADING":
    case "TOGGLESAVING":
      return { ...state, ...rest };

    //   case "TOGGLEERROR":
    //       state.errors = { ...state.errors, ...rest.errors }
    //   return {... state, ...rest};

    case "TOGGLEMODAL":
      state.modals = { ...state.modals, ...rest.modal };
      return { ...state };

    default:
      return state;
  }
};
export default reducerConfig;
