import { changeItens, updateOrcamentoPrime } from "../Actions";
import { SaveOrcamento } from "../SaveOrcamento";

export const AddItens = (sku, quantidade) => {
  return (dispatch, getState) => {
    let indexOrcamento = getState().config.orcamentoAtivo;

    let orcamento = getState().orcamentos.salvos[indexOrcamento];
    let indexBranco = orcamento.itens.findIndex((item) => {
      return item.sku === undefined;
    });
    if (sku) {
      sku = parseInt(sku);
      orcamento.itens.push({ sku, quantidade });
      if (indexBranco !== -1) {
        orcamento.itens.splice(indexBranco, 1);
      }
    } else {
      if (indexBranco === -1) {
        orcamento.itens.push({});
      } else {
        orcamento.itens.push({});
        orcamento.itens.splice(indexBranco, 1);
      }
    }

    dispatch(updateOrcamentoPrime(indexOrcamento, orcamento));
  };
};
