const reducerDatabank = (
  state = {
    userInfo: {},
    produtos: [],
    orcamentosPrime: [],
    orcamentosVnda: [],
  },
  action
) => {
  let orcamentos = [...state.orcamentosPrime];
  let { indexItem, indexOrcamento, newOrcamento, newItem } = action;

  switch (action.type) {
    case "UPDATEDATABANK":
      return { ...state, ...action.input };
    case "UPDATEORCAMENTOPRIME":
      return {
        ...state,
        orcamentosPrime: [
          ...orcamentos.slice(0, indexOrcamento),
          newOrcamento,
          ...orcamentos.slice(indexOrcamento + 1, orcamentos.length),
        ],
      };
    case "UPDATEITEM":
      let itens = [...orcamentos[indexOrcamento].itens];
      console.log({
        ...state,
        orcamentosPrime: [
          ...orcamentos.slice(0, indexOrcamento),
          {
            ...orcamentos[indexOrcamento],
            itens: [
              ...itens.slice(0, indexItem),
              newItem,
              itens.slice(indexItem + 1, itens.length),
            ],
          },
          ...orcamentos.slice(indexOrcamento + 1, orcamentos.length),
        ],
      });
      return {
        ...state,
        orcamentosPrime: [
          ...orcamentos.slice(0, indexOrcamento),
          {
            ...newOrcamento,
            itens: [
              ...itens.slice(0, indexItem),
              newItem,
              itens.slice(indexItem + 1, itens.length),
            ],
          },
          ...orcamentos.slice(indexOrcamento + 1, orcamentos.length),
        ],
      };
    case "ADDORCAMENTO":
      state.orcamentosPrime = [...state.orcamentosPrime, action.orcamento];
      return { ...state };
    default:
      return state;
  }
};
export default reducerDatabank;
