import React, { useEffect, useState } from "react";
import { Button, Col, Figure } from "react-bootstrap";
import { BiMinus, BiPlus } from "react-icons/bi";
import { useDispatch } from "react-redux";
import { AdicionarProduto } from "../../../../Redux/Actions/Ecommerce/AdicionarProduto";

export const ProductGridProduct = (props) => {
  let baseLink = "https://buckettelexpress.s3.amazonaws.com/FOTOS+GOFIND/";
  const dispatch = useDispatch();
  const [ativo, setAtivo] = useState(false);
  const [quantidade, setQuantidade] = useState(props.produto.multiplo);
  let decrement = () => {
    if (quantidade > props.produto.multiplo) {
      setQuantidade(quantidade - props.produto.multiplo);
    }
  };
  let increment = () => {
    setQuantidade(quantidade + props.produto.multiplo);
  };
  let addProduto = () => {
    dispatch(AdicionarProduto({ ...props.produto, quantidade }));
  };
  useEffect(() => {
    if (props.orcamentoAtual.itens) {
      let produto = props.orcamentoAtual.itens.find((item) => {
        return props.produto.sku === item.sku;
      });
      if (produto) {
        setAtivo(true);
        setQuantidade(produto.quantidade);
      }
    }
  }, [props]);
  return (
    <Col xs={3} className="text-start" style={{ paddingTop: "20px" }}>
      <Figure>
        <a
          target="_blank"
          rel="noreferrer"
          href={baseLink + props.produto.sku + ".jpg"}
        >
          <Figure.Image
            alt={props.produto.nome}
            src={baseLink + props.produto.sku + ".jpg"}
            style={{ borderRadius: "20px" }}
          />
        </a>
        <Figure.Caption style={{ color: "white" }}>
          <div
            className="quantity-input"
            style={{ backgroundColor: "transparent" }}
          >
            <button
              onClick={decrement}
              className="quantity-input__modifier quantity-input__modifier--left"
            >
              <BiMinus />
            </button>
            <input
              className="quantity-input__screen"
              type="text"
              value={quantidade}
              readOnly
            />
            <button
              onClick={increment}
              className="quantity-input__modifier quantity-input__modifier--right"
            >
              <BiPlus />
            </button>
          </div>
        </Figure.Caption>
        <Figure.Caption style={{ color: "white" }}>
          <Button
            onClick={addProduto}
            variant="outline-light"
            style={{ width: "100%" }}
          >
            {ativo ? "Adicionado" : "Adicionar ao Orcamento"}
          </Button>
        </Figure.Caption>
        <Figure.Caption style={{ color: "white" }}>
          {props.produto.nome}
        </Figure.Caption>
        <Figure.Caption style={{ color: "white" }}>
          Preco: ${props.produto.valor}
        </Figure.Caption>
        <Figure.Caption style={{ color: "white" }}>
          SKU: {props.produto.sku}
        </Figure.Caption>
      </Figure>
    </Col>
  );
};
