import { changeItens } from "../Actions";
import { saveOrcamento } from "../SaveOrcamento";

export const ChangeItens = (sku, quantidade) => {
  return (dispatch, getState) => {
    let orcamentos = getState().orcamentos;
    let indexOrcamento = orcamentos.salvos.findIndex(
      (orcamento) => orcamento.id === orcamentos.atual
    );
    let itens = getState().orcamentos.salvos[indexOrcamento].itens;
    if (!sku || sku === 0) {
      let indexBranco = itens.findIndex((item) => {
        console.log(item.sku);
        item.sku === 0;
      });

      if (indexBranco === -1) {
        itens.push({});
      } else {
        itens.push({});
        itens.splice(indexBranco, 1);
      }
    } else {
      let produtoIndex = itens.findIndex((item) => item.sku === sku);
      if (produtoIndex !== -1) {
        if (quantidade > 0) {
          itens[produtoIndex].quantidade = quantidade;
        } else {
          if (itens.length === 1) {
            itens.push({});
          }
          itens.splice(produtoIndex, 1);
        }
      } else {
        itens.push({ sku, quantidade });
      }
    }

    dispatch(changeItens(itens, indexOrcamento));
    dispatch(saveOrcamento({ index: indexOrcamento }));
  };
};
